mapper.update:
    	update mxk_roles set
    		status	=	'2'  
    	where	instid   =   #{instId} 

			and	id	=	#{id}


			and	rolename	=	#{roleName}

    	update mxk_roles
    	set status='2' 
    	where instid   =   #{instId} 
    	   and id in

				#{item}

mapper.select:
select  distinct
    r.*
from
    mxk_userinfo u,
    mxk_roles   r,
    mxk_role_member rm
where   u.instid    =   #{instId} 
    and r.instid    =   #{instId} 
    and rm.instid   =   #{instId} 
    and rm.memberid = #{value}
    and u.id        = #{value}
    and rm.type     in( 'USER','USER-DYNAMIC')
    and rm.groupid  =   r.id    
    and rm.memberid =   u.id
